const vertices = [
    [-100, -100, -100], [100, -100, -100], [100, 100, -100], [-100, 100, -100],
    [-100, -100, 100],  [100, -100, 100],  [100, 100, 100],  [-100, 100, 100]
];

let angle = 0;

startAnimation(function() {
    const projected = vertices.map(v => {
        let rotated = rotateY(v[0], v[1], v[2], angle);
        rotated = rotateX(rotated.x, rotated.y, rotated.z, angle * 0.5);
        return project3D(rotated.x, rotated.y, rotated.z);
    });

    const triangles = [
        // Front face - bright red
        {x1: projected[4].x, y1: projected[4].y, x2: projected[5].x, y2: projected[5].y, x3: projected[6].x, y3: projected[6].y, color: 0xFF0000},
        {x1: projected[4].x, y1: projected[4].y, x2: projected[6].x, y2: projected[6].y, x3: projected[7].x, y3: projected[7].y, color: 0xFF0000},

        // Back face - dark red
        {x1: projected[0].x, y1: projected[0].y, x2: projected[1].x, y2: projected[1].y, x3: projected[2].x, y3: projected[2].y, color: 0x880000},
        {x1: projected[0].x, y1: projected[0].y, x2: projected[2].x, y2: projected[2].y, x3: projected[3].x, y3: projected[3].y, color: 0x880000},

        // Right face - orange
        {x1: projected[1].x, y1: projected[1].y, x2: projected[5].x, y2: projected[5].y, x3: projected[6].x, y3: projected[6].y, color: 0xFF8800},
        {x1: projected[1].x, y1: projected[1].y, x2: projected[6].x, y2: projected[6].y, x3: projected[2].x, y3: projected[2].y, color: 0xFF8800},

        // Left face - brown
        {x1: projected[0].x, y1: projected[0].y, x2: projected[4].x, y2: projected[4].y, x3: projected[7].x, y3: projected[7].y, color: 0xCC6600},
        {x1: projected[0].x, y1: projected[0].y, x2: projected[7].x, y2: projected[7].y, x3: projected[3].x, y3: projected[3].y, color: 0xCC6600},

        // Top face - yellow
        {x1: projected[3].x, y1: projected[3].y, x2: projected[2].x, y2: projected[2].y, x3: projected[6].x, y3: projected[6].y, color: 0xFFFF00},
        {x1: projected[3].x, y1: projected[3].y, x2: projected[6].x, y2: projected[6].y, x3: projected[7].x, y3: projected[7].y, color: 0xFFFF00},

        // Bottom face - olive
        {x1: projected[0].x, y1: projected[0].y, x2: projected[1].x, y2: projected[1].y, x3: projected[5].x, y3: projected[5].y, color: 0xCCCC00},
        {x1: projected[0].x, y1: projected[0].y, x2: projected[5].x, y2: projected[5].y, x3: projected[4].x, y3: projected[4].y, color: 0xCCCC00}
    ];

    drawTriangles(triangles);
    angle += 0.02;
});

const pyramidVertices = [
    // Base vertices (square at bottom)
    [-80, 80, -80],   // 0: bottom-left
    [80, 80, -80],    // 1: bottom-right
    [80, 80, 80],     // 2: back-right
    [-80, 80, 80],    // 3: back-left
    // Apex (top point)
    [0, -100, 0]      // 4: tip
];

let pyramidAngle = 0;

startAnimation(function() {
    const projected = pyramidVertices.map(v => {
        let rotated = rotateY(v[0], v[1], v[2], pyramidAngle);
        rotated = rotateX(rotated.x, rotated.y, rotated.z, pyramidAngle * 0.3);
        // Shift to upper-right: add 400 to x, subtract 200 from y
        return {
            x: project3D(rotated.x, rotated.y, rotated.z).x + 400,
            y: project3D(rotated.x, rotated.y, rotated.z).y - 200
        };
    });
    
    const triangles = [
        // Base (2 triangles forming square bottom)
        {x1: projected[0].x, y1: projected[0].y, x2: projected[1].x, y2: projected[1].y, x3: projected[2].x, y3: projected[2].y, color: 0x4488FF, alpha: 0.5},
        {x1: projected[0].x, y1: projected[0].y, x2: projected[2].x, y2: projected[2].y, x3: projected[3].x, y3: projected[3].y, color: 0x4488FF, alpha: 0.5},
        
        // Front face
        {x1: projected[0].x, y1: projected[0].y, x2: projected[1].x, y2: projected[1].y, x3: projected[4].x, y3: projected[4].y, color: 0x00FFFF, alpha: 0.6},
        
        // Right face
        {x1: projected[1].x, y1: projected[1].y, x2: projected[2].x, y2: projected[2].y, x3: projected[4].x, y3: projected[4].y, color: 0x00FF88, alpha: 0.6},
        
        // Back face
        {x1: projected[2].x, y1: projected[2].y, x2: projected[3].x, y2: projected[3].y, x3: projected[4].x, y3: projected[4].y, color: 0x88FF00, alpha: 0.6},
        
        // Left face
        {x1: projected[3].x, y1: projected[3].y, x2: projected[0].x, y2: projected[0].y, x3: projected[4].x, y3: projected[4].y, color: 0xFFFF00, alpha: 0.6}
    ];
    
    drawTriangles(triangles);
    pyramidAngle += 0.03;
});
